private void jButton_loginActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton_loginActionPerformed

    if ((jTextField_username.getText().isEmpty()) && (jPasswordField_password.getText().isEmpty()))
    {
        JOptionPane.showMessageDialog(null,"Enter username and password to continue","Invalid Login Data",JOptionPane.ERROR_MESSAGE);
    }else
    {
        try {
            String username = jTextField_username.getText();
            String password = jPasswordField_password.getText(); //gettext() method is invalid in passwordfield since it violates security Measurements
            
            Database db = new Database();
            ResultSet set1 = db.GetInformation("select password from travelguide where username='"+username+"'"); //get the necessary password for the given username from the DB
            
            while (set1.next()) //go through the taken resultsets till the value is found
            {                
                String dbpass = set1.getString("password"); // getting the necessary column value from the resultset
                if (dbpass.equals(password)) //condition check for password validity
                {
                    travelguide TG = new travelguide();
                    TG.travelguideuser(username);
                    TG.setVisible(true);
                    this.dispose();
                }
                   else {
                    JOptionPane.showMessageDialog(rootPane,"Inceorrect Password");
                }
            }
        } catch (Exception e) {
        }
    }
}//GEN-LAST:event_jButton_loginActionPerformed

